import React, { useState } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import LoadingSpinner from './LoadingSpinner';
import { useNavigate } from 'react-router-dom';

const SimulationForm = ({ onSimulationComplete }) => {
  const [formData, setFormData] = useState({
    algorithm: 'FCFS',
    numVms: 4,
    numCloudlets: 10
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.name === 'algorithm'
        ? e.target.value
        : parseInt(e.target.value)
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await fetch('http://localhost:8080/api/simulation/run', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      if (!response.ok) throw new Error('Simulation failed. Please try again.');

      const data = await response.json();
      onSimulationComplete(data);
      navigate('/results');

    } catch (err) {
      setError(err.message || 'Something went wrong.');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <LoadingSpinner message="Running Simulation..." />;

  return (
    <Card className="shadow-sm">
      <Card.Body>
        <h4 className="mb-4">üõ†Ô∏è Simulation Setup</h4>
        {error && <Alert variant="danger">{error}</Alert>}
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Scheduling Algorithm</Form.Label>
            <Form.Select name="algorithm" value={formData.algorithm} onChange={handleChange}>
              <option value="FCFS">First Come First Serve (FCFS)</option>
              <option value="RR">Round Robin</option>
              <option value="SJF">Shortest Job First (SJF)</option>
              <option value="PSO">Particle Swarm Optimization (PSO)</option>
              {/* Add more algorithms as needed */}
            </Form.Select>
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Number of VMs</Form.Label>
            <Form.Control
              type="number"
              name="numVms"
              value={formData.numVms}
              min={1}
              onChange={handleChange}
              placeholder="Enter number of virtual machines"
              required
            />
          </Form.Group>

          <Form.Group className="mb-4">
            <Form.Label>Number of Cloudlets</Form.Label>
            <Form.Control
              type="number"
              name="numCloudlets"
              value={formData.numCloudlets}
              min={1}
              onChange={handleChange}
              placeholder="Enter number of tasks"
              required
            />
          </Form.Group>

          <Button variant="primary" type="submit" className="w-100">
            üöÄ Run Simulation
          </Button>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default SimulationForm;
