// src/App.js
import React, { useState } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import SimulationForm from './components/SimulationForm';
import SimulationResults from './components/SimulationResults';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AnimatePresence } from 'framer-motion';
import ResultsPage from './pages/ResultsPage';

function App() {
  const [simulationResults, setSimulationResults] = useState(null);

  return (
    <Router>
      <AnimatePresence mode="wait">
        <Routes>
          <Route
            path="/"
            element={
              <Container fluid>
                <Row className="justify-content-center">
                  <Col md={10}>
                    <div className="text-center my-4">
                      <h1>CloudSim CPU Scheduler</h1>
                      <p className="lead">
                        Configure and visualize different CPU scheduling algorithms for cloud computing environments
                      </p>
                    </div>
                    <SimulationForm onSimulationComplete={setSimulationResults} />
                  </Col>
                </Row>
              </Container>
            }
          />
          <Route path="/results" element={<ResultsPage results={simulationResults} />} />
        </Routes>
      </AnimatePresence>
    </Router>
  );
}

export default App;
