import React, { useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col, Card, Alert } from 'react-bootstrap';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const SimulationForm = () => {
  const navigate = useNavigate();

  const [algorithms, setAlgorithms] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({
    schedulingAlgorithm: '',
    numberOfVms: 5,
    vmMips: 1000,
    vmRam: 512,
    vmBw: 1000,
    vmSize: 10000,
    numberOfCloudlets: 10,
    cloudletLength: 10000,
    cloudletPes: 1,
    cloudletFileSize: 300,
    cloudletOutputSize: 300
  });

  useEffect(() => {
    const fetchAlgorithms = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/simulation/algorithms');
        setAlgorithms(response.data);
        if (response.data.length > 0) {
          setFormData(prev => ({ ...prev, schedulingAlgorithm: response.data[0] }));
        }
      } catch (err) {
        setError('❌ Failed to fetch algorithms. Please try again later.');
        console.error('Error fetching algorithms:', err);
      }
    };

    fetchAlgorithms();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'schedulingAlgorithm' ? value : parseInt(value, 10)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const response = await axios.post('http://localhost:8080/api/simulation/run', formData);
      // ✅ Navigate to results page and pass data
      navigate('/results', { state: { results: response.data } });
    } catch (err) {
      setError('❌ Failed to run simulation. Please check your inputs and try again.');
      console.error('Error running simulation:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container>
      <Card className="mt-4 mb-4">
        <Card.Header as="h5">🧪 CPU Scheduling Simulation Parameters</Card.Header>
        <Card.Body>
          {error && <Alert variant="danger">{error}</Alert>}

          <Form onSubmit={handleSubmit}>
            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Scheduling Algorithm</Form.Label>
                  <Form.Select
                    name="schedulingAlgorithm"
                    value={formData.schedulingAlgorithm}
                    onChange={handleChange}
                    required
                  >
                    {algorithms.map(algo => (
                      <option key={algo} value={algo}>
                        {algo.replace(/_/g, ' ')}
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>
            </Row>

            {/* VM & Cloudlet Config Sections (same as before) */}
            {/* ... copy the config inputs from your version here ... */}

            <div className="d-grid gap-2 mt-4">
              <Button variant="primary" type="submit" disabled={loading}>
                {loading ? 'Running Simulation...' : '🚀 Run Simulation'}
              </Button>
            </div>
          </Form>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default SimulationForm;
