import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import SimulationForm from './components/SimulationForm';
import ResultsPage from './pages/ResultsPage';
import { Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Home({ onSimulationComplete }) {
  return (
    <Container className="pt-5">
      <Row className="justify-content-center">
        <Col md={10} className="text-center">
          <h1 className="display-5 fw-bold">☁️ CloudSim Scheduler</h1>
          <p className="lead">Simulate and visualize CPU scheduling algorithms in a cloud environment.</p>
          <SimulationForm onSimulationComplete={onSimulationComplete} />
        </Col>
      </Row>
    </Container>
  );
}

function App() {
  const [simulationResults, setSimulationResults] = useState(null);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home onSimulationComplete={setSimulationResults} />} />
        <Route path="/results" element={<ResultsPage results={simulationResults} />} />
      </Routes>
    </Router>
  );
}

export default App;
