import React, { useState } from 'react';
import { Container, Row, Col, Spinner, Alert } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import SimulationForm from './components/SimulationForm';
import SimulationResults from './components/SimulationResults';
import ErrorBoundary from './components/ErrorBoundary'; // Make sure to create this

function App() {
  const [simulationResults, setSimulationResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [darkMode, setDarkMode] = useState(false);
  const toggleDarkMode = () => setDarkMode(!darkMode);

  const handleSimulationComplete = async (resultsPromise) => {
    try {
      setLoading(true);
      setError(null);
      const results = await resultsPromise; // If SimulationForm returns a promise
      setSimulationResults(results);

      // Scroll to results
      window.scrollTo({
        top: document.getElementById('results-section')?.offsetTop || 0,
        behavior: 'smooth',
      });
    } catch (err) {
      console.error('Simulation error:', err);
      setError('Failed to run simulation. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container fluid>
      <Row className="justify-content-center">
        <Col md={10}>
          <div className="text-center my-4">
            <h1>CloudSim CPU Scheduler</h1>
            <p className="lead">
              Configure and visualize different CPU scheduling algorithms for cloud computing environments
            </p>
          </div>

          <SimulationForm onSimulationComplete={handleSimulationComplete} />

          <div id="results-section" className="mt-4">
            {loading && (
              <div className="text-center my-5">
                <Spinner animation="border" role="status" />
                <div>Running simulation...</div>
              </div>
            )}

            {error && (
              <Alert variant="danger" className="text-center">
                {error}
              </Alert>
            )}

            {!loading && simulationResults && (
              <ErrorBoundary>
                <SimulationResults results={simulationResults} />
              </ErrorBoundary>
            )}
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
