import React, { useState, useEffect } from 'react';
import SimulationResults from './SimulationResults';

function App() {
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate API call
    fetch('/api/simulation/results') // adjust based on your backend
      .then(res => res.json())
      .then(data => {
        setResults(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching simulation results:', err);
        setLoading(false);
      });
  }, []);

  if (loading) return <h4 className="text-center mt-5">‚è≥ Loading simulation results...</h4>;

  return (
    <ErrorBoundary>
      <SimulationResults results={results} />
    </ErrorBoundary>
  );
}

export default App;
