import React, { useState } from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'; // for dark mode and custom styles
import SimulationForm from './components/SimulationForm';
import SimulationResults from './components/SimulationResults';

function App() {
  const [simulationResults, setSimulationResults] = useState(null);
  const [darkMode, setDarkMode] = useState(false);

  const handleSimulationComplete = (results) => {
    setSimulationResults(results);
    const scrollTarget = document.getElementById('results-section');
    if (scrollTarget) {
      scrollTarget.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const toggleDarkMode = () => setDarkMode(!darkMode);

  return (
    <div className={darkMode ? 'app dark-mode' : 'app'}>
      <Container fluid>
        <Row className="justify-content-center">
          <Col md={10}>
            <div className="d-flex justify-content-between align-items-center mt-3 mb-4">
              <div>
                <h1 className="fw-bold">‚òÅÔ∏è CloudSim CPU Scheduler</h1>
                <p className="lead">
                  Configure and visualize CPU scheduling algorithms in cloud environments.
                </p>
              </div>
              <Button variant="outline-primary" onClick={toggleDarkMode}>
                {darkMode ? '‚òÄ Light Mode' : 'üåô Dark Mode'}
              </Button>
            </div>

            <SimulationForm onSimulationComplete={handleSimulationComplete} />

            <div id="results-section">
              {simulationResults && (
                <SimulationResults results={simulationResults} />
              )}
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
