import React, { useState } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import SimulationForm from './components/SimulationForm';
import SimulationResults from './components/SimulationResults';

function App() {
  const [simulationResults, setSimulationResults] = useState(null);

  const handleSimulationComplete = (results) => {
    setSimulationResults(results);
    // Scroll to results
    window.scrollTo({
      top: document.getElementById('results-section')?.offsetTop || 0,
      behavior: 'smooth'
    });
  };

  return (
    <Container fluid>
      <Row className="justify-content-center">
        <Col md={10}>
          <div className="text-center my-4">
            <h1>CloudSim CPU Scheduler</h1>
            <p className="lead">
              Configure and visualize different CPU scheduling algorithms for cloud computing environments
            </p>
          </div>

          <SimulationForm onSimulationComplete={handleSimulationComplete} />

          <div id="results-section">
            {simulationResults && <SimulationResults results={simulationResults} />}
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
